import React, { useState, useEffect } from "react";

function MovieCard ({ movie, onClickSelect }) {
    const BASE_URL = "https://api.themoviedb.org/3";
    const [selectedMovie, setSelectedMovies] = useState([]);

function onClickSelect() {
    console.log(`${movie.title} was clicked!`);
    // useEffect(() => {
    //     fetch(movie.id)
    //     .then(resp => resp.json())
    //     .then(data => {
    //       setSelectedMovies(data.results);
    //     })

    useEffect(() => {
        fetch(`${BASE_URL}/movie/${movie.id}?api_key=96e1ba7547341bdadc80d9ff0f1edbab`)
        .then(resp => resp.json())
        .then(data => {
            console.log(data.results);
            setSelectedMovies(data.results);
        });
    }, []);

} 


    // add onClick handler when clicked fetch movie by id, see endpoints list

    //set state of current movie to API response, show cardand add details to compnent
    return(
        <img src={`${BASE_URL}${movie.poster_path}`} alt={`${movie.title} Poster`} onClick={onClickSelect}></img>
    );
}

export default MovieCard;
