import React from "react";

function MovieCard ({ movie, onClickSelect }) {
    const BASE_URL = "https://image.tmdb.org/t/p/w500/";
    const [movies, setPopularList] = useState([]);

function onClickSelect() {
    fetch(movie.id)
    .then(resp => resp.json())
    .then(data => {
      setPopularList(data.results);
    })
}
    // add onClick handler when clicked fetch movie by id, see endpoints list

    //set state of current movie to API response, show cardand add details to compnent
    return(
        <img src={`${BASE_URL}${movie.poster_path}`} alt={`${movie.title} Poster`} onClick={onClickSelect}></img>
    );
}

export default MovieCard;
